DROP TABLE IF EXISTS `character_base_attribute_bonus`;
CREATE TABLE `character_base_attribute_bonus` (
	`charId` INT NOT NULL,
	`isDual` TINYINT NOT NULL DEFAULT 0,
	`strBonus` SMALLINT NOT NULL DEFAULT 0,
	`conBonus` SMALLINT NOT NULL DEFAULT 0,
	`dexBonus` SMALLINT NOT NULL DEFAULT 0,
	`intBonus` SMALLINT NOT NULL DEFAULT 0,
	`witBonus` SMALLINT NOT NULL DEFAULT 0,
	`menBonus` SMALLINT NOT NULL DEFAULT 0,
	`updatedAt` TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
	PRIMARY KEY (`charId`, `isDual`)
);
